function App() {
  const [vacations, setVacations] = useState([])
  const [showAddModal, setShowAddModal] = useState(false)
  const [editingVacation, setEditingVacation] = useState(null)
  const [showMobileViewer, setShowMobileViewer] = useState(false)
  const [isMobile, setIsMobile] = useState(false)

  // Load saved vacations from localStorage on component mount
  useEffect(() => {
    const savedVacations = localStorage.getItem('vacationOptions')
    if (savedVacations) {
      setVacations(JSON.parse(savedVacations))
    } else {
      // If no saved data, use default data (either saved default or sample)
      setVacations(loadDefaultData())
    }
  }, [])

  // Save vacations to localStorage whenever they change
  useEffect(() => {
    if (vacations.length > 0) {
      localStorage.setItem('vacationOptions', JSON.stringify(vacations))
    }
  }, [vacations])

  // Detect mobile device and auto-show mobile view
  useEffect(() => {
    const checkMobile = () => {
      const mobile = window.innerWidth <= 768 || /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)
      setIsMobile(mobile)
      if (mobile && vacations.length > 0) {
        setShowMobileViewer(true)
      }
    }
    
    checkMobile()
    window.addEventListener('resize', checkMobile)
    return () => window.removeEventListener('resize', checkMobile)
  }, [vacations.length])

  // Check if this is a shared link (no editing capabilities)
  const isSharedLink = () => {
    // If there's saved default data and no user-specific data, treat as shared link
    const hasDefaultData = localStorage.getItem('defaultVacationOptions')
    const hasUserData = localStorage.getItem('vacationOptions')
    return hasDefaultData && !hasUserData
  }

  const addVacation = (newVacation) => {
    const vacationWithId = {
      ...newVacation,
      id: Date.now()
    }
    setVacations([...vacations, vacationWithId])
    setShowAddModal(false)
  }

  const removeVacation = (id) => {
    setVacations(vacations.filter(v => v.id !== id))
  }

  const editVacation = (vacation) => {
    setEditingVacation(vacation)
    setShowAddModal(true)
  }

  const updateVacation = (updatedVacation) => {
    setVacations(vacations.map(v => 
      v.id === editingVacation.id ? updatedVacation : v
    ))
    setShowAddModal(false)
    setEditingVacation(null)
  }

  const toggleAccommodationSelection = (vacationId, accommodationId) => {
    setVacations(vacations.map(vacation => {
      if (vacation.id === vacationId) {
        return {
          ...vacation,
          accommodations: vacation.accommodations.map(acc => ({
            ...acc,
            selected: acc.id === accommodationId ? !acc.selected : false
          }))
        }
      }
      return vacation
    }))
  }

  const handleModalClose = () => {
    setShowAddModal(false)
    setEditingVacation(null)
  }

  const clearAllData = () => {
    if (window.confirm('Are you sure you want to clear all vacation options? This cannot be undone.')) {
      setVacations([])
      localStorage.removeItem('vacationOptions')
    }
  }

  const resetToSampleData = () => {
    if (window.confirm('Reset to sample vacation options? This will replace your current data.')) {
      setVacations(sampleVacations)
    }
  }

  const saveAsDefault = () => {
    if (window.confirm('Save current vacation options as the new default? This will replace the sample data for all users.')) {
      // Save to localStorage as the new default
      localStorage.setItem('defaultVacationOptions', JSON.stringify(vacations))
      alert('Your vacation options have been saved as the new default!')
    }
  }

  const loadDefaultData = () => {
    const savedDefault = localStorage.getItem('defaultVacationOptions')
    if (savedDefault) {
      return JSON.parse(savedDefault)
    }
    return sampleVacations
  }

  return (
    <div className="container">
      <header className="header">
        <h1>Vacation Comparison</h1>
        <p>Compare your travel options and find the perfect destination</p>
      </header>

                                 {!isSharedLink() && (
                 <div className="controls">
                   <button
                     className="btn btn-primary"
                     onClick={() => setShowAddModal(true)}
                   >
                     <Plus size={18} />
                     Add New Option
                   </button>
                   {vacations.length > 0 && (
                     <button
                       className="btn"
                       onClick={() => setShowMobileViewer(true)}
                     >
                       <Smartphone size={18} />
                       Mobile View
                     </button>
                   )}
                   <div className="data-controls">
                     <button
                       className="btn btn-secondary"
                       onClick={saveAsDefault}
                       title="Save current options as new default"
                     >
                       Save as Default
                     </button>
                     <button
                       className="btn btn-secondary"
                       onClick={resetToSampleData}
                       title="Reset to sample vacation options"
                     >
                       Reset to Samples
                     </button>
                     <button
                       className="btn btn-danger"
                       onClick={clearAllData}
                       title="Clear all vacation options"
                     >
                       Clear All
                     </button>
                   </div>
                 </div>
               )}

      {vacations.length === 0 ? (
        <div className="empty-state">
          <h3>No vacation options yet</h3>
          <p>Add your first vacation option to get started</p>
          <button 
            className="btn btn-primary" 
            onClick={() => setShowAddModal(true)}
          >
            <Plus size={18} />
            Add First Option
          </button>
        </div>
      ) : (
        <div className="comparison-grid">
                     {vacations.map((vacation) => (
             <VacationOption
               key={vacation.id}
               vacation={vacation}
               onRemove={isSharedLink() ? null : removeVacation}
               onEdit={isSharedLink() ? null : editVacation}
               onToggleAccommodation={toggleAccommodationSelection}
               isSharedLink={isSharedLink()}
             />
           ))}
        </div>
      )}

      {showAddModal && (
        <AddOptionModal
          onAdd={addVacation}
          onUpdate={updateVacation}
          onClose={handleModalClose}
          editingVacation={editingVacation}
        />
      )}

                   {showMobileViewer && (
               <MobileVacationViewer
                 vacations={vacations}
                 onClose={() => setShowMobileViewer(false)}
                 onEdit={editVacation}
                 onRemove={removeVacation}
                 onToggleAccommodation={toggleAccommodationSelection}
               />
             )}
    </div>
  )
}

export default App 